reduced_motion.css:
@media (prefers-reduced-motion: reduce) {
  *,
  *::before,
  *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
    scroll-behavior: auto !important;
  }

  /* Preserve opacity for essential feedback */
  .toast,
  .modal-backdrop {
    transition: opacity 0.1s linear !important;
  }

  /* Disable floating animations */
  .whatsapp-float {
    animation: none !important;
  }

  /* Show scroll-reveal content immediately */
  [data-scroll-reveal] {
    opacity: 1 !important;
    transform: none !important;
  }

  /* Static skeleton placeholder */
  .skeleton {
    animation: none !important;
    background: var(--color-neutral-100) !important;
  }
}

cssVariables.easings:
/* Easing tokens */
--ease-out: cubic-bezier(0.16, 1, 0.3, 1);
--ease-in: cubic-bezier(0.7, 0, 0.84, 0);
--ease-in-out: cubic-bezier(0.45, 0, 0.55, 1);
--ease-emphasized: cubic-bezier(0.34, 1.56, 0.64, 1);
--ease-spring: cubic-bezier(0.5, 1.8, 0.5, 0.8);
--ease-linear: linear;

utilityClasses.transitions:
/* Transition utilities */
.transition-fast { transition: all var(--duration-fast) var(--ease-out); }
.transition-normal { transition: all var(--duration-normal) var(--ease-out); }
.transition-slow { transition: all var(--duration-slow) var(--ease-out); }
.transition-colors { transition: color var(--duration-fast) var(--ease-out), background-color var(--duration-fast) var(--ease-out), border-color var(--duration-fast) var(--ease-out); }
.transition-transform { transition: transform var(--duration-normal) var(--ease-out); }
.transition-opacity { transition: opacity var(--duration-normal) linear; }

utilityClasses.animations:
/* Animation utilities */
.animate-fade-in { animation: fade-in var(--duration-slow) var(--ease-out); }
.animate-fade-up { animation: fade-up var(--duration-slow) var(--ease-out); }
.animate-scale-in { animation: scale-in var(--duration-slow) var(--ease-emphasized); }

implementation.keyframeDefinitions:
/* Core keyframes */
@keyframes fade-in {
  from { opacity: 0; }
  to { opacity: 1; }
}

@keyframes fade-up {
  from { opacity: 0; transform: translateY(20px); }
  to { opacity: 1; transform: translateY(0); }
}

@keyframes scale-in {
  from { opacity: 0; transform: scale(0.95); }
  to { opacity: 1; transform: scale(1); }
}

@keyframes gentle-float {
  0%, 100% { transform: translateY(0); }
  50% { transform: translateY(-4px); }
}

@keyframes skeleton-shimmer {
  0% { background-position: -200% 0; }
  100% { background-position: 200% 0; }
}
